import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import '../Styles/playerHome.css';
import DefaultGroundImage from '../assets/default_ground.png';
import PlayerNavbar from '../components/PlayerNavbar';

const PlayerHome = () => {
    const [grounds, setGrounds] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [sports, setSports] = useState([]);
    const [cities, setCities] = useState([]);

    // State for filters and search
    const [selectedSport, setSelectedSport] = useState('');
    const [selectedCity, setSelectedCity] = useState('');
    const [searchQuery, setSearchQuery] = useState('');

    useEffect(() => {
        const fetchGrounds = async () => {
            try {
                const response = await axios.get('http://localhost:8080/api/grounds/getAll');
                const groundsData = response.data;
                
                if (Array.isArray(groundsData) && groundsData.length > 0) {
                    setGrounds(groundsData);

                    // Extract unique sports and cities from the fetched data
                    const uniqueSports = [...new Set(groundsData.map(ground => ground.sport.sname))];
                    const uniqueCities = [...new Set(groundsData.map(ground => ground.gCity))];
                    setSports(uniqueSports);
                    setCities(uniqueCities);
                } else {
                    setGrounds([]);
                }
                setLoading(false);
            } catch (err) {
                console.error("Error fetching grounds:", err);
                setError("Failed to fetch grounds. Please check your backend server.");
                setLoading(false);
            }
        };
        fetchGrounds();
    }, []);

    // Filter grounds based on selected filters and search query
    const filteredGrounds = grounds.filter(ground => {
        const matchesSport = selectedSport === '' || ground.sport.sname === selectedSport;
        const matchesCity = selectedCity === '' || ground.gCity === selectedCity;
        const matchesSearch = ground.gName.toLowerCase().includes(searchQuery.toLowerCase());
        
        return matchesSport && matchesCity && matchesSearch;
    });

    if (loading || error) {
        return (
            <>
                <PlayerNavbar />
                <div className="player-home-container">
                    {loading && <div className="loading-message">Loading grounds...</div>}
                    {error && <div className="error-message">{error}</div>}
                </div>
            </>
        );
    }

    return (
        <>
            <PlayerNavbar />
            <div className="player-home-container">
                <h2>Available Grounds</h2>

                {/* Filter and Search Section */}
                <div className="filters-container">
                    <input 
                        type="text"
                        placeholder="Search by ground name..."
                        value={searchQuery}
                        onChange={(e) => setSearchQuery(e.target.value)}
                        className="search-input"
                    />
                    <select
                        value={selectedSport}
                        onChange={(e) => setSelectedSport(e.target.value)}
                        className="filter-select"
                    >
                        <option value="">All Sports</option>
                        {sports.map((sport, index) => (
                            <option key={index} value={sport}>{sport}</option>
                        ))}
                    </select>
                    <select
                        value={selectedCity}
                        onChange={(e) => setSelectedCity(e.target.value)}
                        className="filter-select"
                    >
                        <option value="">All Cities</option>
                        {cities.map((city, index) => (
                            <option key={index} value={city}>{city}</option>
                        ))}
                    </select>
                </div>

                {/* Grounds List */}
                <div className="grounds-list">
                    {filteredGrounds.length > 0 ? (
                        filteredGrounds.map((ground) => (
                            <div key={ground.gId} className="ground-card">
                                <img 
                                    src={ground.gImages ? `http://localhost:8080/${ground.gImages}` : DefaultGroundImage} 
                                    alt={ground.gName} 
                                    className="ground-image" 
                                    onError={(e) => { 
                                        e.target.onerror = null; 
                                        e.target.src = DefaultGroundImage;
                                    }}
                                />
                                <div className="ground-details">
                                    <h3>{ground.gName + " - " + ground.sport.sname}</h3>
                                    <p>{ground.gDescription}</p>
                                    <div className="ground-footer">
                                        <Link to={`/book-ground/${ground.gId}`}>
                                            <button className="booking-button">Book Now</button>
                                        </Link>
                                        <span className="ground-rate">
                                            ₹ {ground.sport?.srate || 'N/A'} per hour
                                        </span>
                                    </div>
                                </div>
                            </div>
                        ))
                    ) : (
                        <div className="no-grounds-message">
                            No grounds found matching your criteria.
                        </div>
                    )}
                </div>
            </div>
        </>
    );
};

export default PlayerHome;
